using System;using System.Collections;using System.Collections.Generic;using System.Linq;using UnityEngine;using WebSocketSharp;namespace JumpeeIsland{    public class TutorialController : MonoBehaviour    {        [SerializeField] private Tutorial m_TestFirstTutorial;        [Header("Tutorial tools")] [SerializeField]        private GameObject m_Pointer;        [SerializeField] private Tutorial _currentTutorial;        private EnvironmentManager _environmentManager;        private IFactionController _playerController;        [SerializeField] private int _currentStepIndex;        private TutorialStep _currentStep;        private EntityData _waitingForEntity;        private Vector3 _waitingForPosition;        private GameObject _waitingUIElement;        private Camera _camera;        private int _platformLayer = 1 << 6;        [SerializeField] private bool _isTutorialCompleted;        public void Init(string currentTutorial)        {            _environmentManager = FindObjectOfType<EnvironmentManager>();            _playerController = FindObjectOfType<PlayerFactionController>();            _environmentManager.OnChangeFaction.AddListener(StartTutorial);            GameFlowManager.Instance.OnSelectEntity.AddListener(DetectEntity);            GameFlowManager.Instance.OnStartGame.AddListener(FirstSession);            SavingSystemManager.Instance.OnRemoveEntityData.AddListener(CheckStepCondition);            MainUI.Instance.OnHideAllMenu.AddListener(MarkStepCompleted);            MainUI.Instance.OnBuyBuildingMenu.AddListener(WhenOpenMenus);            MainUI.Instance.OnShowCreatureMenu.AddListener(WhenOpenMenus);            _camera = Camera.main;            if (m_TestFirstTutorial != null)                LoadTutorial(m_TestFirstTutorial);        }        private void FirstSession(long arg0)        {            StartTutorial();        }        // private void Update()        // {        //     if (Input.anyKeyDown)        //         MarkStepCompleted();        // }        private void WhenOpenMenus(List<JIInventoryItem> arg0)        {            MarkStepCompleted();        }        private void MarkStepCompleted()        {            if (_currentTutorial == null || _currentStep == null)                return;            if (_isTutorialCompleted)                return;            if (_currentStep.CheckPosition)            {                var moveRay = _camera.ScreenPointToRay(Input.mousePosition);                if (!Physics.Raycast(moveRay, out var moveHit, 100f, _platformLayer))                    return;                var pos = moveHit.transform.position;                if (Vector3.Distance(_waitingForPosition, pos) < 0.1f)                    StepCompletedDecide();            }            if (_currentStep.CheckEndCondition)            {                if (_currentStep.EndCondition.PassCondition())                    StepCompletedDecide();            }            if (_currentStep.ButtonSelection)            {                if (_waitingUIElement != null)                    MainUI.Instance.OnSwitchButtonPointer.Invoke(_waitingUIElement.transform.position,                        _waitingUIElement.activeInHierarchy);            }        }        private void LoadTutorial(string tutorialAddress)        {            if (tutorialAddress.IsNullOrEmpty())            {                _currentTutorial = null;                return;            }            Debug.Log("Load new tutorial and update JI_GAME_PROCESS on cloud");            SavingSystemManager.Instance.SaveCurrentTutorial(tutorialAddress);            _currentTutorial = AddressableManager.Instance.GetAddressableSO(tutorialAddress) as Tutorial;        }        // Just for TESTING        private void LoadTutorial(Tutorial tutorial)        {            _currentTutorial = tutorial;        }        #region TUTORIAL PROCESS        private void StartTutorial()        {            if (GameFlowManager.Instance._isGameStarted == false)                return;            if (_environmentManager.GetCurrFaction() == FactionType.Enemy)                return;            if (_isTutorialCompleted)            {                _isTutorialCompleted = false;                HideAllTool();                if (_currentTutorial == null)                    return;                LoadTutorial(_currentTutorial.GetNextTutorial());            }            if (_currentTutorial == null)                return;            // Check condition and execute tutorial            if (_currentTutorial.CheckExecute() == false)                return;            _currentStepIndex = 0;            _isTutorialCompleted = false;            ExecuteStep();        }        private void ExecuteStep()        {            HideAllTool();            _waitingForEntity = null;            _currentStep = _currentTutorial.GetStep(_currentStepIndex);            if (_currentStep.Pointer)            {                if (_currentStep.EntitySelection)                {                    switch (_currentStep.EntityType)                    {                        case EntityType.PLAYER:                            SelectPlayer();                            break;                        case EntityType.RESOURCE:                            SelectResource();                            break;                        case EntityType.COLLECTABLE:                            SelectCollectable();                            break;                        case EntityType.BUILDING:                            SelectBuilding();                            break;                        case EntityType.ENEMY:                            SelectEnemy();                            break;                    }                }                else if (_currentStep.ButtonSelection)                {                    ShowButtonPointer();                }            }            if (_currentStep.Spawner)                SpawnHintObject();        }        private void SelectPlayer()        {            var playerCreature = SavingSystemManager.Instance.GetEnvironmentData().PlayerData[0];            if (_currentStep.CheckEntity)                _waitingForEntity = playerCreature;            ShowPointerAtPlayer(playerCreature);        }        private void SelectCollectable()        {            var collectables = SavingSystemManager.Instance.GetEnvironmentData().CollectableData;            if (collectables.Count == 0)                return;            var collectable = collectables[0];            if (_currentStep.CheckEntity)                _waitingForEntity = collectable;            ShowPointerAtEntity(collectable);        }        private void SelectResource()        {            var resources = SavingSystemManager.Instance.GetEnvironmentData().ResourceData;            if (resources.Count == 0)                return;            var resource = resources.Find(t => t.CollectedCurrency == _currentStep.CurrencyFromResource);            if (resource == null)                return;            if (_currentStep.CheckEntity)                _waitingForEntity = resource;            ShowPointerAtEntity(resource);        }        private void SelectBuilding()        {            var buildings = SavingSystemManager.Instance.GetEnvironmentData().BuildingData;            if (buildings.Count == 0)                return;            var building = buildings.Find(t => t.BuildingType == _currentStep.BuildingType);            if (building == null)                return;            if (_currentStep.CheckEntity)                _waitingForEntity = building;            ShowPointerAtEntity(building);        }                private void SelectEnemy()        {            var enemies = SavingSystemManager.Instance.GetEnvironmentData().EnemyData;            if (enemies.Count == 0)                return;            var enemy = enemies.Find(t => t.CreatureType == _currentStep.CreatureType);            if(enemy == null)                return;                        if (_currentStep.CheckEntity)                _waitingForEntity = enemy;            ShowPointerAtEntity(enemy);        }        private void DetectEntity(EntityData entityData)        {            if (_currentStep == null)                return;            if (_currentStep.CheckPosition || _currentStep.CheckEntity == false)                return;            if (_waitingForEntity == entityData)                StepCompletedDecide();        }        private void StepCompletedDecide()        {            // Move to the next step if current step completed            _currentStepIndex++;            if (_currentTutorial.CheckRunOutOfStep(_currentStepIndex))            {                _isTutorialCompleted = true;                HideAllTool();            }            else                ExecuteStep();        }        private void CheckStepCondition(IRemoveEntity removeEntity)        {            if (_currentStep == null)                return;            if (_currentStep.CheckEndCondition && _currentStep.EndCondition.PassCondition())                _isTutorialCompleted = true;        }        #endregion        #region TUTORIAL TOOLS        private void ShowPointerAtPlayer(CreatureData creatureData)        {            var position = creatureData.Position;            if (_currentStep.ArrowSign)            {                foreach (var direction in Enumerable.Range(1, 4))                {                    var movement = GameFlowManager.Instance.GetEnvManager().GetMovementInspector()                        .MovingPath(creatureData.Position, direction, 0, 0);                    if (movement.jumpCount >= _currentStep.MinJump)                    {                        position = movement.returnPos;                        break;                    }                }            }            if (_currentStep.CheckPosition)            {                if (Vector3.Distance(position, creatureData.Position) < 0.1f)                {                    HideAllTool();                    return;                }                _waitingForPosition = position;            }            PlacePointer(position);        }        private void ShowPointerAtEntity(EntityData entityData)        {            var position = entityData.Position;            _waitingForPosition = position;            PlacePointer(_waitingForPosition);        }        private void PlacePointer(Vector3 position)        {            if (Physics.Raycast(position + Vector3.up * 10f, Vector3.down, out var pointHit, 100f))                position = pointHit.point;            m_Pointer.transform.position = position;            m_Pointer.SetActive(true);        }        private void ShowButtonPointer()        {            _waitingUIElement = MainUI.Instance.GetButtonPosition(_currentStep.ButtonName);            if (_waitingUIElement != null)                MainUI.Instance.OnSwitchButtonPointer.Invoke(_waitingUIElement.transform.position, true);        }        private void SpawnHintObject()        {            var availableTile = GameFlowManager.Instance.GetEnvManager().GetAvailableTile();            if (_currentStep.IsDesignatedPos)                availableTile = _currentStep.DesignatedPos;            if (availableTile != Vector3.negativeInfinity)            {                switch (_currentStep.SpawnType)                {                    case EntityType.RESOURCE:                        SavingSystemManager.Instance.OnSpawnResource(_currentStep.SpawnResource, availableTile);                        break;                    case EntityType.COLLECTABLE:                        SavingSystemManager.Instance.OnSpawnCollectable(_currentStep.SpawnCollectable, availableTile,                            _currentStep.SpawnCollectableLevel);                        break;                    case EntityType.ENEMY:                        SavingSystemManager.Instance.SpawnMovableEntity(_currentStep.SpawnEnemy,availableTile);                        break;                }                StepCompletedDecide();            }        }        private void HideAllTool()        {            m_Pointer.SetActive(false);            MainUI.Instance.OnSwitchButtonPointer.Invoke(Vector3.zero, false);        }        #endregion    }}